onClick:
{{Login_Query.run()
  .then(response => {
    if (response && response.length > 0) {
      const user = response[0];

      // Check if the user is an admin
      if (user.role === "admin") {
        storeValue("user", user);
        showAlert("Login successful!", "success");
        navigateTo("AdminDashboard");
      } else if (user.school_id) {
        // Handle non-admin users who must have a school
        storeValue("user", user);
        showAlert("Login successful!", "success");
        navigateTo("SelectSchoolPage");
      } else {
        // Handle error if a non-admin user doesn't have a school
        showAlert("This user does not have a school assigned. Please contact support.", "error");
      }
    } else {
      // Invalid username or password
      showAlert("Invalid username or password. Please try again.", "error");
    }
  })
  .catch(error => {
    showAlert("Error: " + error.message, "error");
  });
}}
